angular.module('sloanApp', [
    'ui.router',
    'ngAnimate', 
    'appRoutes', 
    'app.factories', 
    'MainCtrl', 
    'HomeCtrl', 
    'PortfolioCtrl', 
    'PortfolioDetailCtrl', 
    'ExperienceCtrl',
    'ContactCtrl'
  ]);
angular.module('appRoutes', []).config(['$stateProvider', '$locationProvider', '$urlRouterProvider', function($stateProvider, $locationProvider, $urlRouterProvider) {

    $urlRouterProvider.otherwise("/");

    $stateProvider

        .state('home', {
            url: '/',
            views: {
                content: {
                    templateUrl: '/templates/home/home.view.html',
                    controller: 'HomeController',
                    controllerAs: 'home'
                }
            }
        })

        .state('portfolio', {
            url: '/portfolio',
            views: {
                content: {
                    templateUrl: '/templates/portfolio/portfolio.view.html',
                    controller: 'PortfolioController',
                    controllerAs: 'portfolio'
                },
                footer: {
                    templateUrl: '/templates/_common/templates/footer.tmpl.html',
                }
            }
        })

            .state('portfolio.detail', {
                url: '/:portfolioId',
                views: {
                    content: {
                        templateUrl: '/templates/portfolio/detail/detail.view.html',
                        controller: 'PortfolioDetailController',
                        controllerAs: 'detail',
                        resolve: {
                            portfolioId: ['$stateParams', function($stateParams){
                                console.log($stateParams.portfolioId);
                                return $stateParams.portfolioId;
                            // }],
                            // portfolioDetail: ['$stateParams', function($stateParams){
                            //   $http.get('/api/portfolio/' + $stateParams.portfolioId)
                            //     .success(function (res) {
                            //       return res;
                            //     });
                            // }],
                            // portfolioPreview: ['$stateParams', function($stateParams){
                            //   $http.get('/api/portfolio/preview/' + $stateParams.portfolioId)
                            //     .success(function (res) {
                            //       return res;
                            //     });
                            }]
                        }
                    },
                    footer: {
                        templateUrl: '/templates/_common/templates/footer.tmpl.html',
                    }
                }
            })


        .state('experience', {
            url: '/experience',
            views: {
                content: {
                    templateUrl: '/templates/experience/experience.view.html',
                    controller: 'ExperienceController',
                    controllerAs: 'experience'
                },
                footer: {
                    templateUrl: '/templates/_common/templates/footer.tmpl.html',
                }
            }
        })

        .state('contact', {
            url: '/contact',
            views: {
                content: {
                    templateUrl: '/templates/contact/contact.view.html',
                    controller: 'ContactController',
                    controllerAs: 'contact'
                },
                footer: {
                    templateUrl: '/templates/_common/templates/footer.tmpl.html',
                }
            }
        });

    $locationProvider.html5Mode({
        enabled: true,
        requireBase: false
    });

}]);
angular.module('ContactCtrl', []).controller('ContactController', function(Page) {
  var vm = this;

  vm.title = 'Contact Me';
  Page.setTitle('Contact Me');

});
angular.module('HomeCtrl', []).controller('HomeController', function(Page) {

  Page.setTitle('Home');
  
});
angular.module('ExperienceCtrl', []).controller('ExperienceController', function($http, Page) {
  var vm = this;
  
  vm.title = 'Experience';
  Page.setTitle('Experience');

  $http.get('api/experience')
    .success(function (res) {
      vm.experience = res;
    });

});
angular.module('PortfolioCtrl', []).controller('PortfolioController', function($http, Page, $location) {
  var vm = this;

  Page.setTitle('Portfolio');
  vm.title = 'Portfolio';

  $http.get('api/portfolio')
    .success(function (res) {
      vm.list = res;
    });

  vm.openItem = function(item) {
    $location.path('portfolio/' + item.id);
  };
});

angular.module('MainCtrl', []).controller('MainController', function($state, $http, Page) {
  var vm = this;

  vm.Page = Page;

  $http.get('/api/navigation')
    .success(function (res) {
      vm.navigation = res;
    });

  vm.view = 'thumb';
  vm.toggleView = function(item) {
    if(item !== vm.view) {
      vm.view = vm.view === 'thumb' ? 'list': 'thumb';
    }
  };

  vm.date = new Date();

});
